import { useState } from "react";
import './OrderStatus.css';
import './Style.css';
function OrderStatus() {
    const [order, setOrder] = useState({
        imgUrl: "data:image/jpeg;base64,",
        id: 'ORD123',
        status: 'Processing',
        customer: 'Arun Kumar',
        orderName: 'Carrot',
        items: 1
    });

    function changeStatus() {
        let e = document.getElementById('item');
        let img = e.value;
        let name = e.options[e.selectedIndex].text;
        setOrder({ ...order, status: document.getElementById('status').value, imgUrl: img, orderName: name });
    }

    function incrementCount() {
        if (order.items !== 15)
            setOrder({ ...order, items: order.items + 1 });
    }

    function decrementCount() {
        if (order.items !== 1)
            setOrder({ ...order, items: order.items - 1 });
    }



    return (
        <div>
            <h1 >Order Details</h1>
            <table border="1">
                <tr>
                    <td>
                        <img src={order.imgUrl} alt="Plate" />
                        <h4>Name: {order.orderName}</h4>
                        <h4>Customer: {order.customer}</h4>
                        <h4>Status: {order.status}</h4>
                        <h4>items: {order.items}</h4>

                    </td>
                    <td>
                        <label >Item Count:</label>
                        <button
                            className="button"
                            onClick={incrementCount}
                            title="Increase the count"
                        >
                            +
                        </button>

                        <button
                            className="button button3"
                            onClick={decrementCount}
                            title="Decrease the count"
                        >
                            -
                        </button><br /> <br />
                        <label >Change Order:</label>
                        <select name="status" id="item" className="custom-select">
                            <option value="data:image/jpeg;base64,">Carrot</option>
                            <option value="data:image/jpeg;base64,">Pumpkin</option>
                            <option value="data:image/jpeg;base64,">Capsicum</option>

                        </select>
                        <br /><br />
                        <label >Change Status:</label>
                        <select name="status" id="status" className="custom-select">
                            <option value="Processing">Processing</option>
                            <option value="Shipped">Shipped</option>
                            <option value="Delivered">Delivered</option>

                        </select>
                        <br /><br />

                        <button className="button" onClick={changeStatus} >Change Status</button>

                    </td>
                </tr>
            </table>






        </div>
    );
}

export default OrderStatus;